const technologys=Array.from(document.querySelectorAll(".technology__link")),descriptions=Array.from(document.querySelectorAll(".description"));for(let o=0;o<technologys.length;o++)technologys[o].addEventListener("click",(function(e){technologys.map(e=>technologys[o].id===e.id?e.classList.add("technology__link--active"):e.classList.remove("technology__link--active")),descriptions.map(e=>technologys[o].id===e.id?e.classList.remove("description_show"):e.classList.add("description_show")),window.scrollTo(0,0)}));